window.KANGXI_RADICALS = [
  { number: 1, radical: "\u4e00", reading: "\u3044\u3061", romaji: "ichi", meaning: "eins", strokes: 1, explanation: "Der einfachste aller Striche – ein waagerechter Strich steht für die Zahl Eins. Er bildet die Basis vieler Kanji wie 三 (drei) und 上 (oben).", examples: ["\u4e03","\u4e09","\u4e0b","\u4e0a","\u4e16"] },
  { number: 2, radical: "\u4e28", reading: "\u305c\u3093", romaji: "zen", meaning: "senkrechter Strich", strokes: 1, explanation: "Ein einzelner senkrechter Strich, der als Grundelement in vielen Kanji vorkommt. Er symbolisiert eine durchgehende Linie und findet sich in 中 (Mitte) und 串 (Spieß).", examples: ["\u4e2d","\u4e32"] },
  { number: 3, radical: "\u4e36", reading: "\u3066\u3093", romaji: "ten", meaning: "Punkt", strokes: 1, explanation: "Ein einzelner Punkt, der kleinste Baustein der Kanji-Schrift. Erscheint als Akzent in vielen Zeichen wie 主 (Herr) und 丹 (Zinnoberrot).", examples: ["\u4e3b","\u4e39"] },
  { number: 4, radical: "\u4e3f", reading: "\u306E", romaji: "no", meaning: "diagonaler Strich", strokes: 1, explanation: "Ein diagonaler Strich von rechts oben nach links unten. Dieser Grundstrich erscheint in Kanji wie 久 (lang/dauerhaft) und 乏 (Mangel).", examples: ["\u4e45","\u4e4f"] },
  { number: 5, radical: "\u4e59", reading: "\u304a\u3064", romaji: "otsu", meaning: "der Zweite", strokes: 1, explanation: "Ursprünglich das zweite Zeichen im chinesischen Zehner-Zyklus. Die geschwungene Form erinnert an einen Haken und findet sich in 九 (neun) und 乾 (trocken).", examples: ["\u4e5d","\u4e7e"] },
  { number: 6, radical: "\u4e85", reading: "\u308f\u304b\u3093\u3080\u308a", romaji: "wakammuri", meaning: "Haken", strokes: 1, explanation: "Ein kleiner Haken, der als Dach über anderen Elementen steht. Er kommt in 了 (beenden) und 予 (im Voraus) vor.", examples: ["\u4e86","\u4e88"] },
  { number: 7, radical: "\u4e8c", reading: "\u306B", romaji: "ni", meaning: "zwei", strokes: 2, explanation: "Zwei waagerechte Striche übereinander bilden die Zahl Zwei. Dieses einfache Prinzip der Strichzählung ist typisch für die frühen Zahlzeichen.", examples: ["\u4e94","\u4e95","\u4e92"] },
  { number: 8, radical: "\u4ea0", reading: "\u306A\u3079\u3076\u305F", romaji: "nabebuta", meaning: "Deckel", strokes: 2, explanation: "Wörtlich »Topfdeckel« – dieses Radikal sitzt wie ein Deckel auf dem Rest des Zeichens. Es findet sich in 交 (kreuzen), 京 (Hauptstadt) und 親 (Eltern).", examples: ["\u4ea4","\u4eac","\u4EB2"] },
  { number: 9, radical: "\u4eba", reading: "\u3072\u3068", romaji: "hito", meaning: "Mensch", strokes: 2, explanation: "Eines der häufigsten Radikale. Als にんべん (Seitenform) steht es links in Kanji, die mit Menschen zu tun haben: 今 (jetzt), 会 (treffen), 体 (Körper).", examples: ["\u4ECA","\u4ED5","\u4ED6","\u4F1A","\u4F53"] },
  { number: 10, radical: "\u513F", reading: "\u306B\u3093\u306B\u3087\u3046", romaji: "ninnyou", meaning: "Beine", strokes: 2, explanation: "Zwei geschwungene Striche, die an menschliche Beine erinnern. Als Fußelement in 元 (Ursprung), 兄 (älterer Bruder) und 光 (Licht).", examples: ["\u5143","\u5144","\u5149","\u514B"] },
  { number: 11, radical: "\u5165", reading: "\u3044\u308B", romaji: "iru", meaning: "eintreten", strokes: 2, explanation: "Zeigt zwei zusammenlaufende Striche, die das Eintreten symbolisieren. Kommt in 全 (ganz/alles) und 内 (innen) vor.", examples: ["\u5168","\u5185"] },
  { number: 12, radical: "\u516B", reading: "\u306F\u3061", romaji: "hachi", meaning: "acht", strokes: 2, explanation: "Zwei auseinanderlaufende Striche symbolisieren die Zahl Acht, die im Japanischen als Glückszahl gilt, da sie sich »öffnet«. Enthalten in 公 (öffentlich) und 六 (sechs).", examples: ["\u516C","\u516D","\u5171","\u5177"] },
  { number: 13, radical: "\u5182", reading: "\u3051\u3044\u304C\u307E\u3048", romaji: "keigamae", meaning: "Umschlie\u00DFung oben", strokes: 2, explanation: "Eine nach unten offene Umschließung, die etwas von oben und den Seiten umgibt. Findet sich in 内 (innen), 冊 (Heft) und 再 (erneut).", examples: ["\u5185","\u518A","\u518D"] },
  { number: 14, radical: "\u5196", reading: "\u308F\u304B\u3093\u3080\u308A", romaji: "wakammuri", meaning: "Krone", strokes: 2, explanation: "Ein Dachzeichen, das an eine Krone oder Bedeckung erinnert. Es sitzt über Zeichen mit Bezug zu Schutz oder Bedeckung, wie 写 (schreiben/kopieren) und 軍 (Armee).", examples: ["\u5199","\u519B","\u51AC"] },
  { number: 15, radical: "\u51AB", reading: "\u306B\u3059\u3044", romaji: "nisui", meaning: "Eis", strokes: 2, explanation: "Zwei Striche links, die gefrorenes Wasser (Eis) darstellen – im Gegensatz zu さんずい (drei Striche = Wasser). Kommt in 冬 (Winter), 冷 (kalt) und 凍 (gefroren) vor.", examples: ["\u51AC","\u51B7","\u51CD"] },
  { number: 16, radical: "\u51E0", reading: "\u304D\u306B\u3087\u3046", romaji: "kinyou", meaning: "Tisch", strokes: 2, explanation: "Stellt einen kleinen Tisch oder Hocker dar. Ein seltenes Radikal, das in 凡 (gewöhnlich) und als Element in 風 (Wind) vorkommt.", examples: ["\u51E1","\u98A8"] },
  { number: 17, radical: "\u51F5", reading: "\u304B\u3093\u306B\u3087\u3046", romaji: "kannyou", meaning: "offener Kasten", strokes: 2, explanation: "Ein nach oben offener Kasten, der etwas aufnimmt. Kommt in 出 (hinausgehen) und 函 (Kasten) vor.", examples: ["\u51FA","\u51FD"] },
  { number: 18, radical: "\u5200", reading: "\u304B\u305F\u306A", romaji: "katana", meaning: "Schwert", strokes: 2, explanation: "Stellt ein japanisches Schwert dar. Als りっとう (Seitenform rechts) erscheint es in vielen Kanji mit Bedeutung von Schneiden oder Trennen: 分 (teilen), 切 (schneiden), 別 (getrennt).", examples: ["\u5206","\u5207","\u5225","\u5236","\u524D"] },
  { number: 19, radical: "\u529B", reading: "\u3061\u304B\u3089", romaji: "chikara", meaning: "Kraft", strokes: 2, explanation: "Ein gebogener Arm symbolisiert Kraft und Stärke. Häufig in Kanji wie 加 (hinzufügen), 労 (Arbeit/Mühe), 動 (bewegen) und 勉 (sich anstrengen).", examples: ["\u52A0","\u52B4","\u52D5","\u52C7","\u52C9"] },
  { number: 20, radical: "\u52F9", reading: "\u3064\u3064\u307F\u304C\u307E\u3048", romaji: "tsutsumigamae", meaning: "umwickeln", strokes: 2, explanation: "Eine umschließende Form, die das Einwickeln oder Umhüllen darstellt. Kommt in 包 (einwickeln) und 化 (verwandeln) vor.", examples: ["\u5305","\u5316"] },
  { number: 21, radical: "\u5315", reading: "\u3055\u3058", romaji: "saji", meaning: "L\u00F6ffel", strokes: 2, explanation: "Stellt einen Löffel oder ein Hilfsmittel dar. Kommt in 化 (verwandeln) und 北 (Norden) vor – bei 北 sitzen zwei Menschen Rücken an Rücken.", examples: ["\u5316","\u5317"] },
  { number: 22, radical: "\u531A", reading: "\u304B\u305F\u304C\u307E\u3048", romaji: "katagamae", meaning: "Kasten offen", strokes: 2, explanation: "Ein nach rechts offener Kasten, der etwas einfasst. Erscheint in 区 (Bezirk), 医 (Medizin) und 匹 (Zähler für Tiere).", examples: ["\u533A","\u533B","\u5339"] },
  { number: 23, radical: "\u5338", reading: "\u304B\u304F\u3057\u304C\u307E\u3048", romaji: "kakushigamae", meaning: "verborgener Kasten", strokes: 2, explanation: "Ähnlich wie Radikal 22, aber mit verborgener Öffnung. Ein seltenes Radikal in 区 (Bezirk) und 匹 (Zähler für Tiere).", examples: ["\u533A","\u5339"] },
  { number: 24, radical: "\u5341", reading: "\u3058\u3085\u3046", romaji: "juu", meaning: "zehn", strokes: 2, explanation: "Ein Kreuz aus waagerechtem und senkrechtem Strich steht für die Zahl Zehn. Enthalten in 千 (tausend), 半 (halb) und 南 (Süden).", examples: ["\u5343","\u5348","\u534A","\u5354","\u5357"] },
  { number: 25, radical: "\u535C", reading: "\u307C\u304F", romaji: "boku", meaning: "Wahrsagerei", strokes: 2, explanation: "Stellt die Risse in einem Schildkrötenpanzer dar, die in der antiken Wahrsagerei gedeutet wurden. Kommt in 占 (wahrsagen) und 卓 (hervorragend) vor.", examples: ["\u5360","\u5353"] },
  { number: 26, radical: "\u5369", reading: "\u308F\u308A\u3075", romaji: "warifu", meaning: "Siegel", strokes: 2, explanation: "Stellt ein amtliches Siegel oder eine Kerbholzhälfte dar. Erscheint in 印 (Siegel/Druck), 危 (gefährlich) und 卵 (Ei).", examples: ["\u5370","\u5371","\u5375"] },
  { number: 27, radical: "\u5382", reading: "\u304C\u3093\u3060\u308C", romaji: "gandare", meaning: "Klippe", strokes: 2, explanation: "Eine überhängende Klippe oder Felsvorsprung. Dieses Radikal umgibt als Dach den Rest des Zeichens, z.B. in 原 (Ursprung/Ebene) und 厳 (streng).", examples: ["\u539F","\u53CE","\u5389"] },
  { number: 28, radical: "\u53B6", reading: "\u3080", romaji: "mu", meaning: "Privatheit", strokes: 2, explanation: "Symbolisiert etwas Privates oder Abgewandtes. Kommt in 去 (weggehen), 参 (teilnehmen) und 反 (entgegengesetzt) vor.", examples: ["\u53BB","\u53C2","\u53CD"] },
  { number: 29, radical: "\u53C8", reading: "\u307E\u305F", romaji: "mata", meaning: "wieder", strokes: 2, explanation: "Stellt eine rechte Hand dar und bedeutet »wieder« oder »auch«. Häufig in 友 (Freund), 取 (nehmen) und 受 (empfangen).", examples: ["\u53CB","\u53CD","\u53D6","\u53D7","\u53CE"] },
  { number: 30, radical: "\u53E3", reading: "\u304F\u3061", romaji: "kuchi", meaning: "Mund", strokes: 3, explanation: "Ein Quadrat stellt einen geöffneten Mund dar. Eines der häufigsten Radikale – es erscheint in Kanji für Sprechen, Essen und Öffnungen: 名 (Name), 味 (Geschmack), 和 (Harmonie).", examples: ["\u53F3","\u540D","\u544A","\u5473","\u548C"] },
  { number: 31, radical: "\u56D7", reading: "\u304F\u306B\u304C\u307E\u3048", romaji: "kunigamae", meaning: "Umschlie\u00DFung", strokes: 3, explanation: "Ein geschlossenes Quadrat, das etwas vollständig umgibt – wie eine Mauer um eine Stadt. Kommt in 国 (Land), 回 (drehen), 図 (Karte) und 団 (Gruppe) vor.", examples: ["\u56DB","\u56DE","\u56E3","\u56FD","\u56F3"] },
  { number: 32, radical: "\u571F", reading: "\u3064\u3061", romaji: "tsuchi", meaning: "Erde", strokes: 3, explanation: "Ein Kreuz auf einer Grundlinie symbolisiert einen Erdhügel. Als つちへん (Seitenform) in 地 (Erde/Boden), 場 (Ort/Platz), 増 (zunehmen) und vielen anderen.", examples: ["\u5730","\u5834","\u5831","\u58F2","\u5897"] },
  { number: 33, radical: "\u58EB", reading: "\u3055\u3080\u3089\u3044", romaji: "samurai", meaning: "Samurai", strokes: 3, explanation: "Ähnlich wie 土 (Erde), aber der obere Strich ist länger. Steht für einen Gelehrten oder Krieger (Samurai). Kommt in 売 (verkaufen) und 声 (Stimme) vor.", examples: ["\u58F2","\u58F0"] },
  { number: 34, radical: "\u5902", reading: "\u3075\u3086\u304C\u3057\u3089", romaji: "fuyugashira", meaning: "folgen", strokes: 3, explanation: "Wörtlich »Winterkopf« – die obere Komponente mancher Kanji. Erscheint in 夏 (Sommer) und 外 (außen). Trotz des Namens hat es keine direkte Verbindung zum Winter.", examples: ["\u590F","\u5916"] },
  { number: 35, radical: "\u590A", reading: "\u3059\u3044\u306B\u3087\u3046", romaji: "suinyou", meaning: "langsam gehen", strokes: 3, explanation: "Stellt langsames, schlurfendes Gehen dar. Ein sehr seltenes Radikal, das hauptsächlich in 夏 (Sommer) als Bestandteil vorkommt.", examples: ["\u590F"] },
  { number: 36, radical: "\u5915", reading: "\u3086\u3046", romaji: "yuu", meaning: "Abend", strokes: 3, explanation: "Eine Mondsichel symbolisiert den Abend und die Dämmerung. Kommt in 外 (außen), 多 (viel), 夜 (Nacht) und 夢 (Traum) vor.", examples: ["\u5916","\u591A","\u591C","\u5922"] },
  { number: 37, radical: "\u5927", reading: "\u3060\u3044", romaji: "dai", meaning: "gro\u00DF", strokes: 3, explanation: "Ein Mensch mit ausgestreckten Armen symbolisiert Größe. Sehr häufiges Radikal in 天 (Himmel), 太 (dick), 夫 (Ehemann) und 奨 (ermutigen).", examples: ["\u5929","\u592A","\u592B","\u5948","\u5968"] },
  { number: 38, radical: "\u5973", reading: "\u304A\u3093\u306A", romaji: "onna", meaning: "Frau", strokes: 3, explanation: "Stellt eine kniende Frau dar. Als おんなへん (Seitenform) in Kanji mit Bezug zu Frauen oder Eigenschaften: 好 (mögen), 妹 (jüngere Schwester), 妻 (Ehefrau).", examples: ["\u597D","\u59B9","\u59BB","\u59D4","\u5B57"] },
  { number: 39, radical: "\u5B50", reading: "\u3053", romaji: "ko", meaning: "Kind", strokes: 3, explanation: "Zeigt ein Kleinkind mit ausgestreckten Armen. Kommt in 字 (Schriftzeichen), 学 (lernen) und 季 (Jahreszeit) vor – Lernen beginnt in der Kindheit.", examples: ["\u5B57","\u5B58","\u5B66","\u5B63"] },
  { number: 40, radical: "\u5B80", reading: "\u3046\u304B\u3093\u3080\u308A", romaji: "ukammuri", meaning: "Dach", strokes: 3, explanation: "Ein Dach über dem Kanji – symbolisiert Haus und Schutz. Sehr häufig in 安 (sicher/günstig), 完 (vollständig), 定 (bestimmen), 実 (Frucht/Wahrheit), 室 (Zimmer).", examples: ["\u5B89","\u5B8C","\u5B9A","\u5B9F","\u5BA4"] },
  { number: 41, radical: "\u5BF8", reading: "\u3059\u3093", romaji: "sun", meaning: "Zoll (Ma\u00DF)", strokes: 3, explanation: "Stellt ein traditionelles Längenmaß dar (ca. 3 cm). Symbolisiert Genauigkeit und Maß. Kommt in 対 (gegenüber), 専 (ausschließlich) und 導 (führen) vor.", examples: ["\u5BFE","\u5C02","\u5C04","\u5C06","\u5C0E"] },
  { number: 42, radical: "\u5C0F", reading: "\u3057\u3087\u3046", romaji: "shou", meaning: "klein", strokes: 3, explanation: "Drei Striche – ein Mittelpunkt, der sich teilt – symbolisieren etwas Kleines. Kommt in 少 (wenig) und 尖 (spitz) vor.", examples: ["\u5C11","\u5C16"] },
  { number: 43, radical: "\u5C22", reading: "\u307E\u3052\u3042\u3057", romaji: "mageashi", meaning: "lahm", strokes: 3, explanation: "Stellt ein gebeugtes oder lahmes Bein dar. Als Dachelement in 尻 (Gesäß), 尾 (Schwanz), 届 (liefern), 屋 (Haus/Laden) und 展 (ausstellen).", examples: ["\u5C3B","\u5C3E","\u5C40","\u5C4B","\u5C55"] },
  { number: 44, radical: "\u5C38", reading: "\u3057\u304B\u3070\u306D", romaji: "shikabane", meaning: "Leichnam", strokes: 3, explanation: "Ursprünglich ein liegender Körper. Heute als Dachform in Kanji für Körperteile und Räume: 尻 (Gesäß), 尾 (Schwanz), 局 (Amt), 屋 (Haus), 展 (ausstellen).", examples: ["\u5C3B","\u5C3E","\u5C40","\u5C4B","\u5C55"] },
  { number: 45, radical: "\u5C6E", reading: "\u3066\u3064", romaji: "tetsu", meaning: "Spross", strokes: 3, explanation: "Stellt einen jungen Pflanzentrieb dar. Ein sehr seltenes Radikal, das vor allem in der Struktur von 山 (Berg) eine Rolle spielt.", examples: ["\u5C71"] },
  { number: 46, radical: "\u5C71", reading: "\u3084\u307E", romaji: "yama", meaning: "Berg", strokes: 3, explanation: "Drei Gipfel symbolisieren einen Berg. Als やまへん (Seitenform) in vielen Natur-Kanji: 岳 (Gipfel), 島 (Insel), 崎 (Vorgebirge), 峠 (Bergpass).", examples: ["\u5CB3","\u5CF6","\u5D0E","\u5CE0","\u5D29"] },
  { number: 47, radical: "\u5DDD", reading: "\u304B\u308F", romaji: "kawa", meaning: "Fluss", strokes: 3, explanation: "Drei fließende Linien stellen einen Fluss dar. Kommt in 州 (Provinz/Staat) und 巣 (Nest) vor. Eines der anschaulichsten Piktogramme.", examples: ["\u5DDE","\u5DE3"] },
  { number: 48, radical: "\u5DE5", reading: "\u3053\u3046", romaji: "kou", meaning: "Arbeit", strokes: 3, explanation: "Zwei waagerechte Striche verbunden durch einen senkrechten – ein Werkzeug oder Werkstück. Kommt in 左 (links) und 巨 (riesig) vor.", examples: ["\u5DE6","\u5DE8","\u5DE6"] },
  { number: 49, radical: "\u5DF1", reading: "\u304A\u306E\u308C", romaji: "onore", meaning: "sich selbst", strokes: 3, explanation: "Ein gewundenes Zeichen, das auf die eigene Person verweist. Kommt in 巳 (Schlange, 6. Tierkreiszeichen) und 巴 (Kommaform) vor.", examples: ["\u5DF3","\u5DF4"] },
  { number: 50, radical: "\u5DFE", reading: "\u306F\u3070", romaji: "haba", meaning: "Tuch", strokes: 3, explanation: "Ein hängendes Tuch oder Stoffstreifen. Als はばへん (Seitenform) in Kanji für Textilien und Flächen: 市 (Stadt/Markt), 布 (Stoff), 希 (Hoffnung), 帝 (Kaiser), 帯 (Gürtel).", examples: ["\u5E02","\u5E03","\u5E0C","\u5E1D","\u5E2F"] },
  { number: 51, radical: "\u5E72", reading: "\u304B\u3093", romaji: "kan", meaning: "trocken", strokes: 3, explanation: "Ein Stößel oder Stampfer – symbolisiert Trocknen und Einfluss. Kommt in 平 (flach/Frieden), 年 (Jahr) und 幸 (Glück) vor.", examples: ["\u5E73","\u5E74","\u5E78"] },
  { number: 52, radical: "\u5E7A", reading: "\u3044\u3068\u304C\u3057\u3089", romaji: "itogashira", meaning: "klein / fein", strokes: 3, explanation: "Winzige Fäden symbolisieren etwas Feines oder Kleines. Erscheint in 幻 (Illusion) und 幼 (kindlich/jung).", examples: ["\u5E7B","\u5E7C"] },
  { number: 53, radical: "\u5E7F", reading: "\u307E\u3060\u308C", romaji: "madare", meaning: "Schr\u00E4gdach", strokes: 3, explanation: "Ein schräges Dach, das den Rest des Zeichens von oben-links bedeckt. Kommt in Gebäude-Kanji vor: 広 (weit), 店 (Laden), 府 (Regierungssitz), 度 (Grad), 床 (Boden).", examples: ["\u5E83","\u5E97","\u5E9C","\u5EA6","\u5E8A"] },
  { number: 54, radical: "\u5EFE", reading: "\u3044\u3093\u306B\u3087\u3046", romaji: "innyou", meaning: "langer Schritt", strokes: 3, explanation: "Stellt einen langen Schritt oder Marsch dar. Kommt in 建 (bauen) und 延 (verlängern) vor – beides erfordert Fortbewegung.", examples: ["\u5EFA","\u5EF6"] },
  { number: 55, radical: "\u5F10", reading: "\u306B\u3058\u3085\u3046", romaji: "nijuu", meaning: "zwanzig", strokes: 3, explanation: "Zwei Zehn-Zeichen ergeben Zwanzig. Kommt in 弁 (unterscheiden/Ventil) und 弄 (spielen mit) vor.", examples: ["\u5F01","\u5F04"] },
  { number: 56, radical: "\u5F0B", reading: "\u3057\u304D\u304C\u307E\u3048", romaji: "shikigamae", meaning: "Armbrust-Schaft", strokes: 3, explanation: "Stellt den Schaft einer Armbrust dar. Kommt in 式 (Zeremonie/Formel) und 引 (ziehen) vor.", examples: ["\u5F0F","\u5F15"] },
  { number: 57, radical: "\u5F13", reading: "\u3086\u307F", romaji: "yumi", meaning: "Bogen", strokes: 3, explanation: "Ein gespannter Bogen. Als ゆみへん (Seitenform) in 引 (ziehen), 弱 (schwach), 強 (stark) und 張 (spannen) – alle mit Bogenspannung verbunden.", examples: ["\u5F15","\u5F31","\u5F37","\u5F35"] },
  { number: 58, radical: "\u5F50", reading: "\u3051\u3044\u304C\u3057\u3089", romaji: "keigashira", meaning: "Schweinkopf", strokes: 3, explanation: "Wörtlich »Schweinkopf« – ein Kopfteil-Radikal. Erscheint in 当 (treffen/zuständig) und 形 (Form).", examples: ["\u5F53","\u5F62"] },
  { number: 59, radical: "\u5F61", reading: "\u3055\u3093\u3065\u304F\u308A", romaji: "sanzukuri", meaning: "drei Striche (Muster)", strokes: 3, explanation: "Drei parallele diagonale Striche auf der rechten Seite – symbolisieren Muster, Licht oder Dekoration. In 形 (Form), 彩 (Buntheit) und 影 (Schatten).", examples: ["\u5F62","\u5F69","\u5F71"] },
  { number: 60, radical: "\u5F73", reading: "\u304E\u3087\u3046\u306B\u3093\u3079\u3093", romaji: "gyouninben", meaning: "Schritt (links)", strokes: 3, explanation: "Zeigt einen Fuß auf einer Straße – steht für Gehen und Bewegung. Sehr häufig: 後 (danach), 待 (warten), 徒 (zu Fuß), 得 (bekommen), 復 (zurückkehren).", examples: ["\u5F8C","\u5F85","\u5F92","\u5F97","\u5FA9"] },
  { number: 61, radical: "\u5FC3", reading: "\u3053\u3053\u308D", romaji: "kokoro", meaning: "Herz", strokes: 4, explanation: "Stellt das Herz dar – Sitz der Gefühle und Gedanken. Als りっしんべん (Seitenform) oder したごころ (Fußform) in 念 (Gedanke), 思 (denken), 情 (Gefühl), 感 (empfinden), 意 (Absicht).", examples: ["\u5FF5","\u601D","\u60C5","\u611F","\u610F"] },
  { number: 62, radical: "\u6208", reading: "\u307B\u3053", romaji: "hoko", meaning: "Hellebarde", strokes: 4, explanation: "Eine Stangenwaffe – die Hellebarde. Kommt in Kanji mit Bezug zu Krieg und Kampf vor: 成 (werden/gelingen), 我 (ich), 戦 (Krieg/Kampf), 戯 (spielen).", examples: ["\u6210","\u6211","\u6226","\u622F"] },
  { number: 63, radical: "\u6236", reading: "\u3068", romaji: "to", meaning: "T\u00FCr", strokes: 4, explanation: "Eine einflügelige Tür (im Gegensatz zu 門, dem zweiflügeligen Tor). Kommt in 所 (Ort), 戻 (zurückkehren), 房 (Raum) und 扇 (Fächer) vor.", examples: ["\u6240","\u623B","\u623F","\u6247"] },
  { number: 64, radical: "\u624B", reading: "\u3066", romaji: "te", meaning: "Hand", strokes: 4, explanation: "Eine Hand mit gespreizten Fingern. Als てへん (Seitenform) eines der häufigsten Radikale: 打 (schlagen), 持 (halten), 指 (Finger/zeigen), 探 (suchen), 推 (schieben).", examples: ["\u6253","\u6301","\u6307","\u63A2","\u63A8"] },
  { number: 65, radical: "\u652F", reading: "\u3057", romaji: "shi", meaning: "Zweig", strokes: 4, explanation: "Ein Ast oder Zweig, der abzweigt. Kommt in 改 (ändern) und 放 (freilassen) vor – Abzweigungen vom Hauptweg.", examples: ["\u6539","\u653E"] },
  { number: 66, radical: "\u6534", reading: "\u307C\u304F\u3065\u304F\u308A", romaji: "bokuzukuri", meaning: "schlagen", strokes: 4, explanation: "Eine Hand, die einen Stock hält – symbolisiert Schlagen oder Einwirken. Rechte Seite in 改 (ändern), 放 (freilassen), 教 (lehren) und 整 (ordnen).", examples: ["\u6539","\u653E","\u6548","\u6559","\u6574"] },
  { number: 67, radical: "\u6587", reading: "\u3076\u3093", romaji: "bun", meaning: "Literatur", strokes: 4, explanation: "Ursprünglich ein tätowiertes oder gemustertes Zeichen – steht für Schrift und Kultur. Kommt in 斉 (gleich) und 斑 (Fleck) vor.", examples: ["\u6589","\u6591"] },
  { number: 68, radical: "\u6597", reading: "\u3068", romaji: "to", meaning: "Sch\u00F6pfkelle", strokes: 4, explanation: "Eine Schöpfkelle oder ein Hohlmaß (ca. 18 Liter). Auch das Sternbild »Großer Wagen«. Kommt in 料 (Gebühr/Material) und 斜 (schräg) vor.", examples: ["\u6599","\u659C"] },
  { number: 69, radical: "\u65A4", reading: "\u304A\u306E", romaji: "ono", meaning: "Axt", strokes: 4, explanation: "Eine Axt zum Holzfällen. Kommt in 新 (neu) – wörtlich: mit der Axt einen neuen Baum fällen –, 断 (abschneiden) und 施 (durchführen) vor.", examples: ["\u65B0","\u65AD","\u65BD"] },
  { number: 70, radical: "\u65B9", reading: "\u304B\u305F", romaji: "kata", meaning: "Richtung", strokes: 4, explanation: "Stellt einen Pflug oder eine Richtungsangabe dar. Kommt in 旅 (Reise), 族 (Stamm/Familie) vor – Bewegung in eine Richtung.", examples: ["\u65C5","\u65CF","\u65E5"] },
  { number: 71, radical: "\u65E0", reading: "\u306A\u3044", romaji: "nai", meaning: "nicht", strokes: 4, explanation: "Bedeutet »nicht vorhanden« oder Verneinung. Kommt in 既 (bereits) und 早 (früh) als Bestandteil vor. Seltenes Radikal.", examples: ["\u65E2","\u65E9"] },
  { number: 72, radical: "\u65E5", reading: "\u3072", romaji: "hi", meaning: "Sonne / Tag", strokes: 4, explanation: "Ein Kreis mit einem Punkt in der Mitte – die Sonne. Eines der wichtigsten Radikale: 明 (hell), 時 (Zeit), 曜 (Wochentag), 書 (schreiben), 最 (am meisten).", examples: ["\u660E","\u6642","\u66DC","\u66F8","\u6700"] },
  { number: 73, radical: "\u66F0", reading: "\u3044\u308F\u304F", romaji: "iwaku", meaning: "sagen", strokes: 4, explanation: "Ähnlich wie 日 (Sonne), aber breiter – ein geöffneter Mund, der spricht. Kommt in 更 (weiter/erneuern) und 替 (ersetzen) vor.", examples: ["\u66F4","\u66FF"] },
  { number: 74, radical: "\u6708", reading: "\u3064\u304D", romaji: "tsuki", meaning: "Mond / Monat", strokes: 4, explanation: "Eine Mondsichel – steht für den Mond und Monate. Als つきへん oft mit にくづき (Fleisch) verwechselt, da beide gleich aussehen. In 有 (haben), 服 (Kleidung), 朝 (Morgen), 期 (Zeitraum).", examples: ["\u6709","\u670D","\u671D","\u671F","\u6728"] },
  { number: 75, radical: "\u6728", reading: "\u304D", romaji: "ki", meaning: "Baum", strokes: 4, explanation: "Ein Baum mit Stamm, Ästen und Wurzeln. Als きへん (Seitenform) in Holz- und Pflanzen-Kanji: 本 (Buch/Ursprung), 村 (Dorf), 林 (Wäldchen), 楽 (Vergnügen), 機 (Maschine).", examples: ["\u672C","\u6751","\u6797","\u697D","\u6A5F"] },
  { number: 76, radical: "\u6B20", reading: "\u3042\u304F\u3073", romaji: "akubi", meaning: "G\u00E4hnen / fehlen", strokes: 4, explanation: "Ein Mensch mit offenem Mund – gähnt oder ruft. Steht rechts in Kanji und bedeutet oft Mangel oder Sehnsucht. In 次 (nächster), 歌 (Lied) und 欲 (Begierde).", examples: ["\u6B21","\u6B4C","\u6B62"] },
  { number: 77, radical: "\u6B62", reading: "\u3068\u3081\u308B", romaji: "tomeru", meaning: "anhalten", strokes: 4, explanation: "Ein Fußabdruck, der stehen bleibt – symbolisiert Anhalten und Gehen. In 正 (richtig), 歩 (gehen), 歴 (Geschichte) und 死 (Tod – das Aufhören zu leben).", examples: ["\u6B63","\u6B69","\u6B74","\u6B7B"] },
  { number: 78, radical: "\u6B79", reading: "\u304C\u3064\u3078\u3093", romaji: "gatsuhen", meaning: "Tod / Zerst\u00F6rung", strokes: 4, explanation: "Knochensplitter symbolisieren Tod und Verfall. Nicht zu verwechseln mit 月 (Mond). Kommt in 死 (Tod), 残 (übrig bleiben) und 段 (Stufe) vor.", examples: ["\u6B7B","\u6B8B","\u6BB5"] },
  { number: 79, radical: "\u6BB3", reading: "\u307B\u3053\u3065\u304F\u308A", romaji: "hokozukuri", meaning: "Waffe", strokes: 4, explanation: "Eine Schlagwaffe oder Keule – steht rechts in 段 (Stufe/Absatz) und 殿 (Palast/Herr). Symbolisiert kontrollierte Gewalt.", examples: ["\u6BB5","\u6BBF"] },
  { number: 80, radical: "\u6BCB", reading: "\u306A\u304B\u308C", romaji: "nakare", meaning: "Mutter (nicht)", strokes: 4, explanation: "Bedeutet »nicht tun« als Verbot. Verwandt mit 母 (Mutter), aber mit anderem Querstrich. Kommt in 毎 (jedes Mal) und 毒 (Gift) vor.", examples: ["\u6BCE","\u6BD2"] },
  { number: 81, radical: "\u6BD4", reading: "\u304F\u3089\u3079\u308B", romaji: "kuraberu", meaning: "vergleichen", strokes: 4, explanation: "Zwei Menschen nebeneinander – sie werden verglichen. Das Kanji 比 selbst bedeutet »vergleichen« und ist gleichzeitig sein eigenes Radikal.", examples: ["\u6BD4"] },
  { number: 82, radical: "\u6BDB", reading: "\u3051", romaji: "ke", meaning: "Fell / Haar", strokes: 4, explanation: "Stellt Haare oder Fell dar – ein Büschel Haare an einem Körper. Das Kanji 毛 selbst bedeutet Haar und ist sein eigenes Radikal.", examples: ["\u6BDB"] },
  { number: 83, radical: "\u6C0F", reading: "\u3046\u3058", romaji: "uji", meaning: "Clan", strokes: 4, explanation: "Ursprünglich ein Löffel oder Spachtel – später für »Clan« oder »Familie« verwendet. Kommt in 民 (Volk) vor, wo viele Familien ein Volk bilden.", examples: ["\u6C11"] },
  { number: 84, radical: "\u6C14", reading: "\u304D\u304C\u307E\u3048", romaji: "kigamae", meaning: "Dampf", strokes: 4, explanation: "Aufsteigende Dampfwolken – die äußere Umschließung von 気 (Geist/Energie/Luft). Ein seltenes Radikal, das nur in wenigen Kanji vorkommt.", examples: ["\u6C17"] },
  { number: 85, radical: "\u6C34", reading: "\u307F\u305A", romaji: "mizu", meaning: "Wasser", strokes: 4, explanation: "Ein fließender Strom mit Spritzern. Als さんずい (drei Tropfen links) eines der häufigsten Radikale: 池 (Teich), 海 (Meer), 深 (tief), 温 (warm), 漢 (China/Kanji).", examples: ["\u6C60","\u6D77","\u6DF1","\u6E29","\u6F22"] },
  { number: 86, radical: "\u706B", reading: "\u3072", romaji: "hi", meaning: "Feuer", strokes: 4, explanation: "Flammen, die aufsteigen. Als ひへん (Seitenform) oder れんが (vier Punkte unten) in 点 (Punkt), 焼 (brennen), 熱 (Hitze) und 照 (beleuchten).", examples: ["\u70B9","\u713C","\u71B1","\u7167"] },
  { number: 87, radical: "\u722A", reading: "\u3064\u3081", romaji: "tsume", meaning: "Klaue", strokes: 4, explanation: "Eine greifende Klaue oder Kralle von oben. Als つめかんむり (Dachform) in Kanji, die Greifen oder Fassen ausdrücken.", examples: ["\u722A","\u7238"] },
  { number: 88, radical: "\u7236", reading: "\u3061\u3061", romaji: "chichi", meaning: "Vater", strokes: 4, explanation: "Eine Hand, die eine Axt hält – Symbol für den Vater als Beschützer und Arbeiter. Das Kanji 父 bedeutet »Vater«.", examples: ["\u7236"] },
  { number: 89, radical: "\u723B", reading: "\u3057\u3087\u3046", romaji: "shou", meaning: "Doppelkreuz", strokes: 4, explanation: "Zwei gekreuzte Striche – auch »Mischung« oder »Verflechtung« genannt. Ein seltenes Radikal, das als Bestandteil in 片 (Stück) vorkommt.", examples: ["\u7247"] },
  { number: 90, radical: "\u723F", reading: "\u3057\u3087\u3046\u3078\u3093", romaji: "shouhen", meaning: "Spalte (links)", strokes: 4, explanation: "Die linke Hälfte eines gespaltenen Holzstücks. Kommt in 版 (Druckplatte/Auflage) und zusammen mit 片 (rechte Hälfte) vor.", examples: ["\u7248","\u7247"] },
  { number: 91, radical: "\u7247", reading: "\u304B\u305F", romaji: "kata", meaning: "Spalte / Seite", strokes: 4, explanation: "Die rechte Hälfte eines gespaltenen Holzstücks. Als eigenständiges Kanji bedeutet 片 »Stück« oder »Seite«. Partner von Radikal 90.", examples: ["\u7247","\u7248"] },
  { number: 92, radical: "\u7259", reading: "\u304D\u3070", romaji: "kiba", meaning: "Fangzahn", strokes: 4, explanation: "Stellt einen Fangzahn oder Stoßzahn dar. Ein seltenes Radikal – 牙 selbst bedeutet »Reißzahn«.", examples: ["\u7259"] },
  { number: 93, radical: "\u725B", reading: "\u3046\u3057", romaji: "ushi", meaning: "Rind", strokes: 4, explanation: "Ein Rinderkopf mit Hörnern von vorne. Als うしへん (Seitenform) in 物 (Ding), 牧 (Weide) und 特 (besonders) – Rinder waren wertvoller Besitz.", examples: ["\u7269","\u7267","\u7279"] },
  { number: 94, radical: "\u72AC", reading: "\u3044\u306C", romaji: "inu", meaning: "Hund", strokes: 4, explanation: "Ein Hund mit erhobenem Schwanz. Als けものへん (Seitenform) in 状 (Zustand), 独 (allein), 猿 (Affe) und 獣 (Bestie) – Tiere und tierisches Verhalten.", examples: ["\u72B6","\u72EC","\u733F","\u7363"] },
  { number: 95, radical: "\u7384", reading: "\u3052\u3093", romaji: "gen", meaning: "dunkel / geheimnisvoll", strokes: 5, explanation: "Symbolisiert das Dunkle, Mysteriöse und Tiefgründige. Kommt in 率 (Rate/Verhältnis) und als Bestandteil in 王 (König) vor.", examples: ["\u7387","\u738B"] },
  { number: 96, radical: "\u738B", reading: "\u305F\u307E", romaji: "tama", meaning: "Juwel / K\u00F6nig", strokes: 4, explanation: "Drei Querstriche durch einen Senkrechten – ursprünglich ein Jadeschmuck. Als たまへん (Seitenform) in 現 (Gegenwart), 理 (Vernunft), 環 (Ring) und 玉 (Edelstein).", examples: ["\u73FE","\u7406","\u7389","\u74B0"] },
  { number: 97, radical: "\u74DC", reading: "\u3046\u308A", romaji: "uri", meaning: "Melone", strokes: 5, explanation: "Stellt eine Melone dar – die Ranken und die Frucht. Ein seltenes Radikal, 瓜 selbst bedeutet »Melone« oder »Kürbisgewächs«.", examples: ["\u74DC"] },
  { number: 98, radical: "\u74E6", reading: "\u304B\u308F\u3089", romaji: "kawara", meaning: "Dachziegel", strokes: 5, explanation: "Stellt gebrannte Tonziegel dar. Kommt in 瓶 (Flasche) vor. Dachziegel (瓦) waren ein wichtiges Baumaterial im alten Japan.", examples: ["\u74F6","\u74E6"] },
  { number: 99, radical: "\u7518", reading: "\u3042\u307E\u3044", romaji: "amai", meaning: "s\u00FC\u00DF", strokes: 5, explanation: "Etwas Süßes im Mund – der Querstrich im Mund-Zeichen symbolisiert den süßen Geschmack. 甘 selbst bedeutet »süß« und ist ein seltenes Radikal.", examples: ["\u7518"] },
  { number: 100, radical: "\u751F", reading: "\u3044\u304D\u308B", romaji: "ikiru", meaning: "Leben", strokes: 5, explanation: "Eine Pflanze, die aus der Erde wächst – symbolisiert Leben und Wachstum. 生 ist vielseitig: »leben«, »geboren werden«, »roh«. Kommt in 産 (Produktion) und 用 (benutzen) vor.", examples: ["\u751F","\u7523","\u7528"] },
  { number: 101, radical: "\u7528", reading: "\u3082\u3061\u3044\u308B", romaji: "mochiiru", meaning: "benutzen", strokes: 5, explanation: "Ein Zaun oder Rahmen – symbolisiert Nutzung und Gebrauch. 用 selbst bedeutet »benutzen« und ist sein eigenes Radikal.", examples: ["\u7528"] },
  { number: 102, radical: "\u7530", reading: "\u305F", romaji: "ta", meaning: "Reisfeld", strokes: 5, explanation: "Ein in vier Parzellen unterteiltes Reisfeld von oben betrachtet. Als たへん (Seitenform) in 男 (Mann – wer auf dem Feld arbeitet), 町 (Stadtviertel), 界 (Grenze), 番 (Nummer).", examples: ["\u7537","\u753A","\u754C","\u756A","\u7559"] },
  { number: 103, radical: "\u758B", reading: "\u3072\u304D\u3078\u3093", romaji: "hikihen", meaning: "Stoffrolle", strokes: 5, explanation: "Stellt eine Rolle Stoff oder Tuch dar. Kommt in 疎 (entfremdet) und 疑 (zweifeln) vor.", examples: ["\u758E","\u7591"] },
  { number: 104, radical: "\u7592", reading: "\u3084\u307E\u3044\u3060\u308C", romaji: "yamaidare", meaning: "Krankheit", strokes: 5, explanation: "Ein Mensch, der auf einem Bett liegt – symbolisiert Krankheit. Umgibt Kanji wie 病 (Krankheit), 痛 (Schmerz), 療 (Heilung) und 癒 (genesen).", examples: ["\u75C5","\u75DB","\u7642","\u7662"] },
  { number: 105, radical: "\u7676", reading: "\u306F\u3064\u304C\u3057\u3089", romaji: "hatsugashira", meaning: "auseinander", strokes: 5, explanation: "Zwei Füße, die auseinandergehen. Kommt in 発 (abfeuern/losfahren) und 登 (klettern) vor – beides erfordert den ersten Schritt.", examples: ["\u767A","\u767B"] },
  { number: 106, radical: "\u767D", reading: "\u3057\u308D", romaji: "shiro", meaning: "wei\u00DF", strokes: 5, explanation: "Ein Sonnentropfen – die Farbe Weiß als Symbol für Reinheit und Klarheit. Kommt in 百 (hundert), 的 (Ziel), 皆 (alle) und 皇 (Kaiser) vor.", examples: ["\u767E","\u7684","\u7686","\u7687"] },
  { number: 107, radical: "\u76AE", reading: "\u304B\u308F", romaji: "kawa", meaning: "Haut / Fell", strokes: 5, explanation: "Eine Hand, die ein Fell abzieht. Nicht zu verwechseln mit 革 (Leder, Radikal 177). 皮 steht für die rohe, unbearbeitete Haut. Kommt in 波 (Welle) vor.", examples: ["\u76AE","\u6CE2"] },
  { number: 108, radical: "\u76BF", reading: "\u3055\u3089", romaji: "sara", meaning: "Teller", strokes: 5, explanation: "Ein flacher Teller oder Schale von der Seite. Kommt in 盆 (Schüssel/Bon-Fest), 益 (Nutzen), 盗 (stehlen) und 監 (überwachen) vor.", examples: ["\u76C6","\u76CA","\u76D7","\u76E3"] },
  { number: 109, radical: "\u76EE", reading: "\u3081", romaji: "me", meaning: "Auge", strokes: 5, explanation: "Ein auf die Seite gedrehtes Auge. Als めへん (Seitenform) in 直 (direkt), 看 (pflegen/schauen), 着 (anziehen/ankommen), 知 (wissen) und 研 (schleifen/forschen).", examples: ["\u76F4","\u770B","\u7740","\u77E5","\u7814"] },
  { number: 110, radical: "\u77DB", reading: "\u307B\u3053", romaji: "hoko", meaning: "Speer", strokes: 5, explanation: "Ein langer Speer. Das Kanji 矛 ist bekannt aus dem Wort 矛盾 (mujun – Widerspruch), wörtlich »Speer und Schild« – eine berühmte chinesische Parabel.", examples: ["\u77DB"] },
  { number: 111, radical: "\u77E2", reading: "\u3084", romaji: "ya", meaning: "Pfeil", strokes: 5, explanation: "Ein Pfeil mit Spitze und Schaft. Kommt in 知 (wissen – ein Pfeil, der sein Ziel trifft) und 短 (kurz) vor.", examples: ["\u77E5","\u77ED"] },
  { number: 112, radical: "\u77F3", reading: "\u3044\u3057", romaji: "ishi", meaning: "Stein", strokes: 5, explanation: "Ein Felsblock unter einer Klippe. Als いしへん (Seitenform) in 研 (schleifen/forschen), 破 (zerbrechen), 確 (sicher/bestätigen) und 磁 (Magnet).", examples: ["\u7814","\u7834","\u78BA","\u78C1"] },
  { number: 113, radical: "\u793A", reading: "\u3057\u3081\u3059", romaji: "shimesu", meaning: "zeigen / G\u00F6tter", strokes: 5, explanation: "Ein Altar, auf dem den Göttern Opfer dargebracht werden. Als しめすへん (Seitenform) in religiösen Kanji: 社 (Schrein/Firma), 神 (Gott), 禁 (verboten), 福 (Glück), 祈 (beten).", examples: ["\u793E","\u795E","\u7981","\u798F","\u7948"] },
  { number: 114, radical: "\u79B9", reading: "\u3050\u3046\u306E\u3042\u3057", romaji: "guunoashi", meaning: "Spur", strokes: 5, explanation: "Tierspuren oder Fußabdrücke. Ein seltenes Radikal, das in 秀 (hervorragend) und 私 (ich/privat) als Bestandteil vorkommt.", examples: ["\u79C0","\u79C1"] },
  { number: 115, radical: "\u79BE", reading: "\u306E\u304E\u3078\u3093", romaji: "nogihen", meaning: "Getreide", strokes: 5, explanation: "Eine Getreidepflanze mit hängender Ähre. Als のぎへん (Seitenform) in 私 (ich), 科 (Fach), 秋 (Herbst – Erntezeit), 種 (Art/Samen), 積 (anhäufen).", examples: ["\u79C1","\u79D1","\u79CB","\u7A2E","\u7A4D"] },
  { number: 116, radical: "\u7A74", reading: "\u3042\u306A", romaji: "ana", meaning: "H\u00F6hle", strokes: 5, explanation: "Ein Dach über einer Öffnung – eine Höhle oder ein Loch. Als あなかんむり (Dachform) in 空 (leer/Himmel), 究 (ergründen), 突 (stoßen), 窓 (Fenster).", examples: ["\u7A7A","\u7A76","\u7A81","\u7A93","\u7ACB"] },
  { number: 117, radical: "\u7ACB", reading: "\u305F\u3064", romaji: "tatsu", meaning: "stehen", strokes: 5, explanation: "Ein Mensch, der aufrecht auf dem Boden steht. Kommt in 章 (Kapitel), 端 (Ende/Rand) und 競 (wetteifern) vor.", examples: ["\u7AE0","\u7AEF","\u7AF6"] },
  { number: 118, radical: "\u7AF9", reading: "\u305F\u3051", romaji: "take", meaning: "Bambus", strokes: 6, explanation: "Zwei Bambusblätter – Bambus ist Symbol für Biegsamkeit und Stärke. Als たけかんむり (Dachform) in 第 (Nummer), 等 (Klasse/gleich), 答 (Antwort), 算 (rechnen), 管 (Rohr/verwalten).", examples: ["\u7B2C","\u7B49","\u7B54","\u7B97","\u7BA1"] },
  { number: 119, radical: "\u7C73", reading: "\u3053\u3081", romaji: "kome", meaning: "Reis", strokes: 6, explanation: "Reiskörner um einen Stiel – Reis als Grundnahrungsmittel Japans. Kommt in 米 (Reis/Amerika), 精 (Essenz/raffiniert) und 粉 (Pulver/Mehl) vor.", examples: ["\u7C73","\u7CBE","\u7D04"] },
  { number: 120, radical: "\u7CF8", reading: "\u3044\u3068", romaji: "ito", meaning: "Faden / Seide", strokes: 6, explanation: "Seidenfäden von einem Kokon. Als いとへん (Seitenform) sehr häufig in 約 (Versprechen), 索 (Seil), 細 (fein), 終 (Ende), 経 (Erfahrung).", examples: ["\u7D04","\u7D22","\u7D30","\u7D42","\u7D4C"] },
  { number: 121, radical: "\u7F36", reading: "\u304B\u3093", romaji: "kan", meaning: "Tongef\u00E4\u00DF", strokes: 6, explanation: "Ein großes Tongefäß zum Aufbewahren. Seltenes Radikal – 缶 bedeutet heute auch »Dose«.", examples: ["\u7F36","\u7F46"] },
  { number: 122, radical: "\u7F51", reading: "\u3042\u307F\u304C\u3057\u3089", romaji: "amigashira", meaning: "Netz", strokes: 6, explanation: "Ein Netz von oben – als あみがしら (Dachform) in Kanji mit Bezug zu Fangen und Ordnen: 置 (platzieren), 署 (Amt) und 罪 (Verbrechen).", examples: ["\u7F6E","\u7F72","\u7F8A"] },
  { number: 123, radical: "\u7F8A", reading: "\u3072\u3064\u3058", romaji: "hitsuji", meaning: "Schaf", strokes: 6, explanation: "Ein Schaf mit Hörnern von vorne. Schafe galten als sanft und schön, daher in 美 (schön) und 群 (Gruppe/Herde).", examples: ["\u7F8E","\u7FA4","\u7FBD"] },
  { number: 124, radical: "\u7FBD", reading: "\u306F\u306D", romaji: "hane", meaning: "Feder / Fl\u00FCgel", strokes: 6, explanation: "Zwei Federn oder Flügel. In 羽 (Feder), 習 (lernen – Vögel üben das Fliegen) und 翼 (Flügel).", examples: ["\u7FBD","\u7FD2","\u7FFC"] },
  { number: 125, radical: "\u8001", reading: "\u304A\u3044", romaji: "oi", meaning: "alt", strokes: 6, explanation: "Ein alter Mann mit Stock und langem Haar. In 老 (alt), 考 (nachdenken – Weisheit des Alters) und 者 (Person).", examples: ["\u8001","\u8003","\u8005"] },
  { number: 126, radical: "\u800C", reading: "\u3057\u304B\u3057\u3066", romaji: "shikashite", meaning: "und dabei", strokes: 6, explanation: "Ein Barthaar am Kinn – symbolisiert »und dabei« oder »aber«. Kommt in 耐 (ertragen/aushalten) vor.", examples: ["\u8010"] },
  { number: 127, radical: "\u8012", reading: "\u3059\u304D\u3078\u3093", romaji: "sukihen", meaning: "Pflug", strokes: 6, explanation: "Ein Pflug, der die Erde bearbeitet. In 耕 (pflügen) und 耗 (verbrauchen) – beides mit landwirtschaftlicher Arbeit verbunden.", examples: ["\u8015","\u8017"] },
  { number: 128, radical: "\u8033", reading: "\u307F\u307F", romaji: "mimi", meaning: "Ohr", strokes: 6, explanation: "Ein Ohr von der Seite. Als みみへん (Seitenform) in 聞 (hören), 職 (Beruf) und 聖 (heilig) – alle mit Zuhören und Verstehen verbunden.", examples: ["\u8033","\u805E","\u8077","\u8056"] },
  { number: 129, radical: "\u807F", reading: "\u3075\u3067", romaji: "fude", meaning: "Pinsel", strokes: 6, explanation: "Eine Hand, die einen Pinsel hält. Kommt als Bestandteil in 書 (schreiben) vor – das Schreiben mit dem Pinsel.", examples: ["\u66F8"] },
  { number: 130, radical: "\u8089", reading: "\u306B\u304F", romaji: "niku", meaning: "Fleisch", strokes: 6, explanation: "Streifen von Fleisch. Als にくづき (Seitenform) sieht es wie 月 (Mond) aus, bedeutet aber Körper/Fleisch. In 肩 (Schulter), 育 (aufziehen), 背 (Rücken), 能 (Fähigkeit), 腕 (Arm).", examples: ["\u80A9","\u80B2","\u80CC","\u80FD","\u8155"] },
  { number: 131, radical: "\u81E3", reading: "\u3057\u3093", romaji: "shin", meaning: "Untertan", strokes: 6, explanation: "Ein knieender Mensch mit gesenktem Blick – ein Untertan vor seinem Herrscher. In 臣 (Vasall) und 臨 (beiwohnen/bevorstehen).", examples: ["\u81E3","\u81E8"] },
  { number: 132, radical: "\u81EA", reading: "\u307F\u305A\u304B\u3089", romaji: "mizukara", meaning: "sich selbst", strokes: 6, explanation: "Ursprünglich eine Nase – in Japan zeigt man auf die eigene Nase, um »ich selbst« zu sagen. 自 bedeutet »selbst«.", examples: ["\u81EA"] },
  { number: 133, radical: "\u81F3", reading: "\u3044\u305F\u308B", romaji: "itaru", meaning: "ankommen", strokes: 6, explanation: "Ein Pfeil, der sein Ziel erreicht – symbolisiert Ankommen und Erreichen. Kommt in 致 (herbeiführen) vor.", examples: ["\u81F4"] },
  { number: 134, radical: "\u81FC", reading: "\u3046\u3059", romaji: "usu", meaning: "M\u00F6rser", strokes: 6, explanation: "Ein Mörser zum Zerstampfen von Reis oder Medizin. Kommt in 興 (Interesse/aufblühen) vor.", examples: ["\u8208"] },
  { number: 135, radical: "\u820C", reading: "\u3057\u305F", romaji: "shita", meaning: "Zunge", strokes: 6, explanation: "Eine Zunge, die aus dem Mund herausragt. 舌 kommt in 舗 (Laden/pflastern) vor – die Zunge zum Schmecken und Sprechen.", examples: ["\u820C","\u8217"] },
  { number: 136, radical: "\u821B", reading: "\u307E\u3044\u3042\u3057", romaji: "maiashi", meaning: "entgegen", strokes: 6, explanation: "Zwei Füße, die gegeneinander gerichtet sind – symbolisiert Tanzen oder Entgegengehen. Kommt in 舞 (tanzen) vor.", examples: ["\u821E"] },
  { number: 137, radical: "\u821F", reading: "\u3075\u306D", romaji: "fune", meaning: "Boot", strokes: 6, explanation: "Ein Boot von der Seite. Als ふねへん (Seitenform) in 船 (Schiff), 航 (Seefahrt) und 艦 (Kriegsschiff).", examples: ["\u8239","\u822A","\u8266"] },
  { number: 138, radical: "\u826E", reading: "\u3053\u3093", romaji: "kon", meaning: "gut / aufh\u00F6ren", strokes: 6, explanation: "Symbolisiert etwas Gutes oder das Aufhören einer Handlung. Kommt in 良 (gut) und 色 (Farbe) als Bestandteil vor.", examples: ["\u826F","\u8272"] },
  { number: 139, radical: "\u8272", reading: "\u3044\u308D", romaji: "iro", meaning: "Farbe", strokes: 6, explanation: "Ursprünglich ein knieender Mensch mit einem anderen – symbolisiert Schönheit und Farbe. 色 bedeutet »Farbe«.", examples: ["\u8272"] },
  { number: 140, radical: "\u8278", reading: "\u304F\u3055\u304B\u3093\u3080\u308A", romaji: "kusakammuri", meaning: "Gras / Pflanze", strokes: 6, explanation: "Zwei Grashalme als Dach über Pflanzen-Kanji. Eines der häufigsten Radikale: 花 (Blume), 英 (hervorragend), 茶 (Tee), 草 (Gras), 薬 (Medizin).", examples: ["\u82B1","\u82F1","\u8336","\u8349","\u85AC"] },
  { number: 141, radical: "\u864D", reading: "\u3080\u3057\u3078\u3093", romaji: "mushihen", meaning: "Tiger", strokes: 6, explanation: "Tigerstreifen – stellt einen sich duckenden Tiger dar. Als とらがしら (Dachform) in 虚 (leer/vergeblich) und 虎 (Tiger).", examples: ["\u865A","\u864E"] },
  { number: 142, radical: "\u866B", reading: "\u3080\u3057", romaji: "mushi", meaning: "Insekt", strokes: 6, explanation: "Eine Schlange oder ein Insekt – im alten China umfasste »Insekt« alle kleinen Kriechtiere. Als むしへん (Seitenform) in vielen Tiernamen.", examples: ["\u866B","\u8840"] },
  { number: 143, radical: "\u8840", reading: "\u3061", romaji: "chi", meaning: "Blut", strokes: 6, explanation: "Ein Gefäß mit Blut darin. 血 selbst bedeutet »Blut«. Kommt in Wörtern wie 血液 (Blut) und 出血 (Blutung) vor.", examples: ["\u8840","\u884C"] },
  { number: 144, radical: "\u884C", reading: "\u304E\u3087\u3046", romaji: "gyou", meaning: "gehen / Reihe", strokes: 6, explanation: "Eine Kreuzung – symbolisiert Gehen und Reihen. Als ぎょうがまえ (umschließende Form) in 行 (gehen), 術 (Technik), 街 (Straße) und 表 (Oberfläche).", examples: ["\u884C","\u8853","\u8857","\u8868"] },
  { number: 145, radical: "\u8863", reading: "\u3053\u308D\u3082", romaji: "koromo", meaning: "Kleidung", strokes: 6, explanation: "Ein Gewand mit Kragen und Ärmeln. Als ころもへん (Seitenform) in 被 (erleiden), 装 (ausstatten), 裏 (Rückseite), 補 (ergänzen), 複 (doppelt).", examples: ["\u88AB","\u88C5","\u88CF","\u88DC","\u8907"] },
  { number: 146, radical: "\u897E", reading: "\u306B\u3057", romaji: "nishi", meaning: "Westen", strokes: 6, explanation: "Ein Vogelnest – Vögel kehren bei Sonnenuntergang (im Westen) zurück. Als Dachform in 要 (brauchen), 覆 (bedecken) und 見 (sehen).", examples: ["\u8981","\u8986","\u898B"] },
  { number: 147, radical: "\u898B", reading: "\u307F\u308B", romaji: "miru", meaning: "sehen", strokes: 7, explanation: "Ein großes Auge auf Beinen – aktives Sehen und Beobachten. In 見 (sehen), 親 (Eltern/nah), 観 (betrachten), 規 (Regel) und 覚 (erwachen).", examples: ["\u898B","\u89AA","\u89B3","\u89D2","\u898F"] },
  { number: 148, radical: "\u89D2", reading: "\u3064\u306E", romaji: "tsuno", meaning: "Horn", strokes: 7, explanation: "Ein Tierhorn – symbolisiert auch Ecken und Winkel. In 角 (Winkel/Horn), 解 (lösen/verstehen) und 触 (berühren).", examples: ["\u89D2","\u89E3","\u89E6"] },
  { number: 149, radical: "\u8A00", reading: "\u3053\u3068\u3070", romaji: "kotoba", meaning: "Sprache / Wort", strokes: 7, explanation: "Schallwellen aus einem Mund. Als ごんべん (Seitenform) eines der wichtigsten Radikale: 言 (sagen), 計 (planen), 記 (aufschreiben), 話 (sprechen), 読 (lesen).", examples: ["\u8A00","\u8A08","\u8A18","\u8A71","\u8AAD"] },
  { number: 150, radical: "\u8C37", reading: "\u305F\u306B", romaji: "tani", meaning: "Tal", strokes: 7, explanation: "Wasser, das zwischen Bergen fließt – ein Tal. 谷 selbst bedeutet »Tal« und ist ein eigenständiges Kanji.", examples: ["\u8C37"] },
  { number: 151, radical: "\u8C46", reading: "\u307E\u3081", romaji: "mame", meaning: "Bohne", strokes: 7, explanation: "Ein Gefäß mit Bohnen darin. Kommt in 豊 (reichlich/üppig) vor – Bohnen als Symbol für Überfluss.", examples: ["\u8C4A","\u8C46"] },
  { number: 152, radical: "\u8C55", reading: "\u3044\u306E\u3053", romaji: "inoko", meaning: "Schwein", strokes: 7, explanation: "Ein Schwein oder Wildschwein. Kommt in 家 (Haus – früher lebten Schweine im Haus) und 象 (Elefant) vor.", examples: ["\u5BB6","\u8C61"] },
  { number: 153, radical: "\u8C78", reading: "\u3080\u3058\u306A", romaji: "mujina", meaning: "Dachs / Wildkatze", strokes: 7, explanation: "Ein katzenartiges Raubtier. Kommt in 貞 (keusch/treu) und 貨 (Waren/Währung) vor.", examples: ["\u8C9E","\u8CA8"] },
  { number: 154, radical: "\u8C9D", reading: "\u304B\u3044", romaji: "kai", meaning: "Muschel / Geld", strokes: 7, explanation: "Eine Muschel – im alten China als Zahlungsmittel verwendet. Als かいへん (Seitenform) in Geld-Kanji: 販 (verkaufen), 買 (kaufen), 資 (Kapital), 質 (Qualität), 贈 (schenken).", examples: ["\u8CA9","\u8CB7","\u8CC7","\u8CEA","\u8D08"] },
  { number: 155, radical: "\u8D64", reading: "\u3042\u304B", romaji: "aka", meaning: "rot", strokes: 7, explanation: "Erde und Feuer ergeben die Farbe Rot. 赤 selbst bedeutet »rot« und symbolisiert auch Aufrichtigkeit.", examples: ["\u8D64","\u8D70"] },
  { number: 156, radical: "\u8D70", reading: "\u306F\u3057\u308B", romaji: "hashiru", meaning: "laufen", strokes: 7, explanation: "Ein laufender Mensch mit schwingenden Armen. Als そうにょう (umschließende Form) in 走 (laufen), 起 (aufstehen), 超 (übertreffen) und 趣 (Geschmack/Charme).", examples: ["\u8D70","\u8D77","\u8D85","\u8DB3"] },
  { number: 157, radical: "\u8DB3", reading: "\u3042\u3057", romaji: "ashi", meaning: "Fu\u00DF / Bein", strokes: 7, explanation: "Ein Knie und ein Fuß – symbolisiert Gehen und Treten. Als あしへん (Seitenform) in 足 (Fuß), 路 (Weg), 踏 (treten) und 踊 (tanzen).", examples: ["\u8DB3","\u8DEF","\u8E0F","\u8E0A"] },
  { number: 158, radical: "\u8EAB", reading: "\u307F", romaji: "mi", meaning: "K\u00F6rper", strokes: 7, explanation: "Ein schwangerer Körper von der Seite – symbolisiert den ganzen Körper. 身 bedeutet »Körper« oder »sich selbst«.", examples: ["\u8EAB","\u8ECA"] },
  { number: 159, radical: "\u8ECA", reading: "\u304F\u308B\u307E", romaji: "kuruma", meaning: "Wagen / Rad", strokes: 7, explanation: "Ein Wagen von oben: Achse, Räder und Karosserie. Als くるまへん (Seitenform) in 車 (Wagen), 転 (drehen/umfallen), 載 (laden) und 輪 (Rad/Ring).", examples: ["\u8ECA","\u8EE2","\u8F09","\u8F2A"] },
  { number: 160, radical: "\u8F9B", reading: "\u304B\u3089\u3044", romaji: "karai", meaning: "scharf / bitter", strokes: 7, explanation: "Ein Tätowiernadel-Werkzeug – symbolisiert Strafe und Schärfe. Kommt in 辛 (scharf), 辞 (Wort/aufhören) und 辰 (Drache/Morgen) vor.", examples: ["\u8F9B","\u8FAD","\u8FB0"] },
  { number: 161, radical: "\u8FB0", reading: "\u305F\u3064", romaji: "tatsu", meaning: "Morgenzeit", strokes: 7, explanation: "Das fünfte Tierkreiszeichen (Drache) – steht für die Morgenstunden. Kommt in 農 (Landwirtschaft) vor.", examples: ["\u8FB0","\u8FB2","\u8FBA"] },
  { number: 162, radical: "\u8FBA", reading: "\u3057\u3093\u306B\u3087\u3046", romaji: "shinnyou", meaning: "Weg (gehen)", strokes: 7, explanation: "Ein Fuß auf einem Weg – symbolisiert Fortbewegung. Eines der häufigsten Radikale: 近 (nah), 送 (schicken), 通 (durchgehen), 速 (schnell), 進 (voranschreiten).", examples: ["\u8FD1","\u9001","\u901A","\u901F","\u9032"] },
  { number: 163, radical: "\u9091", reading: "\u304A\u304A\u3056\u3068", romaji: "oozato", meaning: "Stadt (rechts)", strokes: 7, explanation: "Steht rechts und symbolisiert eine Stadt oder Siedlung. In 部 (Abteilung), 都 (Hauptstadt), 郵 (Post) und 郎 (junger Mann).", examples: ["\u90E8","\u90FD","\u90F5","\u90CE"] },
  { number: 164, radical: "\u9149", reading: "\u3068\u308A\u3078\u3093", romaji: "torihen", meaning: "Sake / Alkohol", strokes: 7, explanation: "Ein Weinkrug – symbolisiert Alkohol und Gärung. In 酌 (einschenken), 配 (verteilen) und 酒 (Sake/Alkohol).", examples: ["\u914C","\u914D","\u9152"] },
  { number: 165, radical: "\u91C6", reading: "\u306E\u3054\u3081\u3078\u3093", romaji: "nogomehen", meaning: "unterscheiden", strokes: 7, explanation: "Getreide wird sortiert und unterschieden. Kommt in 釈 (erklären/freilassen) und 重 (schwer/wichtig) vor.", examples: ["\u91C8","\u91CD"] },
  { number: 166, radical: "\u91CC", reading: "\u3055\u3068", romaji: "sato", meaning: "Dorf / Meile", strokes: 7, explanation: "Feld und Erde bilden ein Dorf. Auch eine Längeneinheit (ca. 4 km). In 重 (schwer), 野 (Feld) und 量 (Menge/messen).", examples: ["\u91CD","\u91CE","\u91CF"] },
  { number: 167, radical: "\u91D1", reading: "\u304B\u306D", romaji: "kane", meaning: "Gold / Metall", strokes: 8, explanation: "Goldnuggets in der Erde. Als かねへん (Seitenform) eines der häufigsten Radikale für Metalle: 鉄 (Eisen), 銀 (Silber), 録 (aufnehmen), 鏡 (Spiegel).", examples: ["\u9244","\u9280","\u9332","\u9593","\u9577"] },
  { number: 168, radical: "\u9577", reading: "\u306A\u304C\u3044", romaji: "nagai", meaning: "lang", strokes: 8, explanation: "Ein alter Mann mit langem Haar – symbolisiert Länge und Zeit. 長 bedeutet sowohl »lang« als auch »Chef/Vorsteher«.", examples: ["\u9577"] },
  { number: 169, radical: "\u9580", reading: "\u3082\u3093", romaji: "mon", meaning: "Tor", strokes: 8, explanation: "Zwei Türflügel – ein großes Tor. Als もんがまえ (umschließende Form) in 門 (Tor), 間 (Zwischenraum), 開 (öffnen), 関 (Beziehung) und 閲 (durchsehen).", examples: ["\u9580","\u9593","\u958B","\u95A2","\u95B2"] },
  { number: 170, radical: "\u961C", reading: "\u3053\u3056\u3068\u3078\u3093", romaji: "kozatohen", meaning: "H\u00FCgel (links)", strokes: 8, explanation: "Stufen eines Hügels – steht links. Im Gegensatz zu おおざと (rechts = Stadt). In 防 (verteidigen), 限 (begrenzen), 院 (Institut), 際 (Gelegenheit), 陸 (Land).", examples: ["\u9632","\u9650","\u9662","\u969B","\u9678"] },
  { number: 171, radical: "\u96B6", reading: "\u308C\u3044", romaji: "rei", meaning: "Sklave", strokes: 8, explanation: "Ein gefangener Mensch – symbolisiert Unterwerfung und Abhängigkeit. Kommt in 難 (schwierig) vor.", examples: ["\u96E3","\u96E8"] },
  { number: 172, radical: "\u96B9", reading: "\u3075\u308B\u3068\u308A", romaji: "furutori", meaning: "kurzschwanziger Vogel", strokes: 8, explanation: "Ein Vogel mit kurzem Schwanz – im Gegensatz zu 鳥 (Vogel mit langem Schwanz). In 集 (sammeln), 雑 (gemischt) und 離 (trennen).", examples: ["\u96C6","\u96D1","\u96E2"] },
  { number: 173, radical: "\u96E8", reading: "\u3042\u3081", romaji: "ame", meaning: "Regen", strokes: 8, explanation: "Regentropfen fallen aus Wolken. Als あめかんむり (Dachform) in Wetter-Kanji: 雪 (Schnee), 雲 (Wolke), 電 (Elektrizität) und 雷 (Donner).", examples: ["\u96EA","\u96F2","\u96FB","\u9752"] },
  { number: 174, radical: "\u9752", reading: "\u3042\u304A", romaji: "ao", meaning: "blau / gr\u00FCn", strokes: 8, explanation: "Pflanzen und klares Wasser – die Farbe Blaugrün. Kommt in 青 (blau/grün), 静 (still/ruhig) und 清 (rein) vor.", examples: ["\u9752","\u9759","\u975E"] },
  { number: 175, radical: "\u975E", reading: "\u3042\u3089\u305A", romaji: "arazu", meaning: "nicht / falsch", strokes: 8, explanation: "Zwei Hälften, die sich abstoßen – symbolisiert Verneinung und Fehler. 非 bedeutet »nicht« oder »falsch«.", examples: ["\u975E","\u9762"] },
  { number: 176, radical: "\u9762", reading: "\u3081\u3093", romaji: "men", meaning: "Gesicht / Fl\u00E4che", strokes: 9, explanation: "Ein Gesicht in einem Rahmen – symbolisiert Oberfläche und Gesicht. 面 bedeutet sowohl »Gesicht« als auch »Fläche« oder »Maske«.", examples: ["\u9762"] },
  { number: 177, radical: "\u9769", reading: "\u304B\u308F", romaji: "kawa", meaning: "Leder", strokes: 9, explanation: "Gegerbtes Leder – im Gegensatz zu 皮 (rohe Haut). In 革 (Leder/Revolution), 鞍 (Sattel) und 靴 (Schuhe).", examples: ["\u9769","\u978D","\u9774"] },
  { number: 178, radical: "\u97CB", reading: "\u306A\u3081\u3057\u304C\u308F", romaji: "nameshigawa", meaning: "weiches Leder", strokes: 9, explanation: "Weich gegerbtes Leder – feiner als 革. Kommt in 韓 (Korea) und 韻 (Reim/Klang) vor.", examples: ["\u97D3","\u97FB"] },
  { number: 179, radical: "\u97ED", reading: "\u306B\u3089", romaji: "nira", meaning: "Lauch", strokes: 9, explanation: "Eine Lauchpflanze. Seltenes Radikal, kommt als Bestandteil in 音 (Laut/Ton) vor.", examples: ["\u97F3"] },
  { number: 180, radical: "\u97F3", reading: "\u304A\u3068", romaji: "oto", meaning: "Laut / Ton", strokes: 9, explanation: "Ein Mund mit Schallwellen – symbolisiert Klang und Ton. 音 (Ton/Laut) kommt in 響 (widerhallen/Echo) vor.", examples: ["\u97F3","\u97FF"] },
  { number: 181, radical: "\u9801", reading: "\u304A\u304A\u304C\u3044", romaji: "oogai", meaning: "Kopf / Seite", strokes: 9, explanation: "Ein Kopf auf einem Körper. Als おおがい (rechte Seite) in 頂 (Gipfel), 順 (Reihenfolge), 頼 (bitten), 題 (Thema) und 顔 (Gesicht).", examples: ["\u9802","\u9806","\u983C","\u984C","\u9854"] },
  { number: 182, radical: "\u98A8", reading: "\u304B\u305C", romaji: "kaze", meaning: "Wind", strokes: 9, explanation: "Ein Insekt im Wind – im alten China glaubte man, Insekten entstünden durch den Wind. 風 bedeutet »Wind« und auch »Stil«.", examples: ["\u98A8"] },
  { number: 183, radical: "\u98DB", reading: "\u3068\u3076", romaji: "tobu", meaning: "fliegen", strokes: 9, explanation: "Ein Kranich mit ausgebreiteten Flügeln im Flug. 飛 bedeutet »fliegen« und ist eines der komplexesten einsilbigen Kanji.", examples: ["\u98DB"] },
  { number: 184, radical: "\u98DF", reading: "\u305F\u3079\u308B", romaji: "taberu", meaning: "Essen", strokes: 9, explanation: "Ein Deckel über einer Schüssel mit Reis – Essen. Als しょくへん (Seitenform) in 飲 (trinken), 養 (ernähren), 館 (Halle/Gebäude) und 餅 (Reiskuchen).", examples: ["\u98F2","\u990A","\u9928","\u9905"] },
  { number: 185, radical: "\u9996", reading: "\u304F\u3073", romaji: "kubi", meaning: "Hals / Kopf", strokes: 9, explanation: "Ein Kopf mit Haaren – symbolisiert den Hals als Verbindung zwischen Kopf und Körper. 首 bedeutet »Hals« und »Anführer«.", examples: ["\u9996","\u9999"] },
  { number: 186, radical: "\u9999", reading: "\u304B\u304A\u308A", romaji: "kaori", meaning: "Duft", strokes: 9, explanation: "Getreide und süßer Duft – der angenehme Geruch von reifem Korn. 香 bedeutet »Duft« und »Weihrauch«.", examples: ["\u9999"] },
  { number: 187, radical: "\u99AC", reading: "\u3046\u307E", romaji: "uma", meaning: "Pferd", strokes: 10, explanation: "Ein Pferd von der Seite mit Mähne und Beinen. Als うまへん (Seitenform) in 馬 (Pferd), 駅 (Bahnhof), 駐 (parken), 騒 (lärmen) und 験 (Erfahrung/prüfen).", examples: ["\u99AC","\u99C5","\u99D0","\u9A12","\u9A13"] },
  { number: 188, radical: "\u9AA8", reading: "\u307B\u306D", romaji: "hone", meaning: "Knochen", strokes: 10, explanation: "Knochen und Gelenke eines Skeletts. 骨 bedeutet »Knochen« und kommt in 體/体 (Körper) als Bestandteil vor.", examples: ["\u9AA8","\u9AD4"] },
  { number: 189, radical: "\u9AD8", reading: "\u305F\u304B\u3044", romaji: "takai", meaning: "hoch", strokes: 10, explanation: "Ein hohes Gebäude mit Turm – symbolisiert Höhe. 高 bedeutet »hoch« und auch »teuer«.", examples: ["\u9AD8"] },
  { number: 190, radical: "\u9ADF", reading: "\u304B\u307F\u304C\u3057\u3089", romaji: "kamigashira", meaning: "langes Haar", strokes: 10, explanation: "Langes, herabhängendes Haar. Kommt in 髪 (Haare) vor – das Haar auf dem Kopf eines Menschen.", examples: ["\u9AEA"] },
  { number: 191, radical: "\u9B25", reading: "\u3068\u3046\u304C\u307E\u3048", romaji: "tougamae", meaning: "Kampf", strokes: 10, explanation: "Zwei kämpfende Personen. Kommt in 闘 (kämpfen) vor – ein intensiver Zweikampf.", examples: ["\u95D8"] },
  { number: 192, radical: "\u9B2D", reading: "\u3061\u3087\u3046", romaji: "chou", meaning: "Opferwein", strokes: 10, explanation: "Ein Gefäß für rituellen Opferwein bei Zeremonien. Seltenes Radikal, kommt in 鬯 vor.", examples: ["\u9B31"] },
  { number: 193, radical: "\u9B32", reading: "\u304B\u304F", romaji: "kaku", meaning: "Dreifuss-Kessel", strokes: 10, explanation: "Ein dreifüßiger Bronzekessel zum Kochen – ein wichtiges Ritualgerät im alten China. Sehr seltenes Radikal.", examples: [] },
  { number: 194, radical: "\u9B3C", reading: "\u304A\u306B", romaji: "oni", meaning: "D\u00E4mon", strokes: 10, explanation: "Ein Geist mit großem Kopf und Hörnern. Kommt in 魂 (Seele) und 魅 (Charme/bezaubern) vor – die übernatürliche Welt.", examples: ["\u9B42","\u9B45"] },
  { number: 195, radical: "\u9B5A", reading: "\u3046\u304A", romaji: "uo", meaning: "Fisch", strokes: 11, explanation: "Ein Fisch mit Kopf, Körper und Schwanzflosse. Als うおへん (Seitenform) in 魚 (Fisch), 鮮 (frisch) und 鯨 (Wal).", examples: ["\u9B5A","\u9BAE","\u9BE8"] },
  { number: 196, radical: "\u9CE5", reading: "\u3068\u308A", romaji: "tori", meaning: "Vogel", strokes: 11, explanation: "Ein Vogel mit langem Schwanz – im Gegensatz zu 隹 (kurzschwanziger Vogel). In 鳥 (Vogel), 鶏 (Huhn) und vielen Vogelnamen.", examples: ["\u9CE5","\u9D8F","\u9E21"] },
  { number: 197, radical: "\u9E75", reading: "\u3057\u304A", romaji: "shio", meaning: "Salz", strokes: 11, explanation: "Salzkristalle in einem Gefäß. Kommt in 塩 (Salz) vor. Salz war im alten Japan äußerst wertvoll.", examples: ["\u5869"] },
  { number: 198, radical: "\u9E7F", reading: "\u3057\u304B", romaji: "shika", meaning: "Hirsch", strokes: 11, explanation: "Ein Hirsch mit Geweih. Kommt in 鹿 (Hirsch) und 麗 (schön/elegant) vor – Hirsche galten als anmutige Tiere.", examples: ["\u9E7F","\u9E97"] },
  { number: 199, radical: "\u9EA6", reading: "\u3080\u304E", romaji: "mugi", meaning: "Weizen", strokes: 11, explanation: "Eine Weizenpflanze mit Ähre. 麦 bedeutet »Weizen« oder »Gerste« – wichtige Getreidesorten.", examples: ["\u9EA6"] },
  { number: 200, radical: "\u9EBB", reading: "\u3042\u3055", romaji: "asa", meaning: "Hanf", strokes: 11, explanation: "Hanfpflanzen unter einem Dach – Hanf wurde zum Weben von Textilien verwendet. 麻 bedeutet »Hanf« und auch »Taubheit«.", examples: ["\u9EBB","\u9EB9"] },
  { number: 201, radical: "\u9EC4", reading: "\u304D\u3044\u308D", romaji: "kiiro", meaning: "gelb", strokes: 11, explanation: "Die Farbe Gelb – im alten China die kaiserliche Farbe. 黄 bedeutet »gelb« und symbolisiert die Erde und Mitte.", examples: ["\u9EC4"] },
  { number: 202, radical: "\u9ECD", reading: "\u304D\u3073", romaji: "kibi", meaning: "Hirse", strokes: 12, explanation: "Eine Hirsepflanze. Hirse war eines der ältesten Grundnahrungsmittel in Ostasien. Sehr seltenes Radikal.", examples: [] },
  { number: 203, radical: "\u9ED2", reading: "\u304F\u308D", romaji: "kuro", meaning: "schwarz", strokes: 11, explanation: "Ruß über einem Feuer – die Farbe Schwarz. 黒 bedeutet »schwarz«. Kommt in 黙 (schweigen) vor – im Dunkeln still sein.", examples: ["\u9ED2","\u9ED9"] },
  { number: 204, radical: "\u9EDB", reading: "\u306C\u3044", romaji: "nui", meaning: "N\u00E4herei / Stickerei", strokes: 12, explanation: "Nadel und Faden für Stickerei. Eines der seltensten Radikale – kommt in modernen Kanji kaum vor.", examples: [] },
  { number: 205, radical: "\u9EF6", reading: "\u3079\u3093\u3042\u3057", romaji: "ben'ashi", meaning: "Frosch", strokes: 13, explanation: "Ein Frosch oder eine Kröte. Sehr seltenes Radikal, das in modernen Kanji praktisch nicht mehr verwendet wird.", examples: [] },
  { number: 206, radical: "\u9F0E", reading: "\u304B\u306A\u3048", romaji: "kanae", meaning: "Dreifuss", strokes: 13, explanation: "Ein dreifüßiges Ritualgefäß aus Bronze. Symbol für Macht und Herrschaft im alten China. Seltenes Radikal.", examples: [] },
  { number: 207, radical: "\u9F13", reading: "\u3064\u3065\u307F", romaji: "tsuzumi", meaning: "Trommel", strokes: 13, explanation: "Eine Trommel auf einem Ständer. 鼓 bedeutet »Trommel« und auch »ermutigen/anregen« – der Trommelschlag motiviert.", examples: ["\u9F13"] },
  { number: 208, radical: "\u9F20", reading: "\u306D\u305A\u307F", romaji: "nezumi", meaning: "Ratte / Maus", strokes: 13, explanation: "Eine Maus oder Ratte – das erste Tier im chinesischen Tierkreis. 鼠 symbolisiert Cleverness und Anpassungsfähigkeit.", examples: ["\u9F20"] },
  { number: 209, radical: "\u9F3B", reading: "\u306F\u306A", romaji: "hana", meaning: "Nase", strokes: 14, explanation: "Eine Nase von vorne – das hervorstechendste Merkmal des Gesichts. 鼻 bedeutet »Nase«. Im Japanischen zeigt man auf die Nase, um »ich« zu sagen.", examples: ["\u9F3B"] },
  { number: 210, radical: "\u9F4A", reading: "\u305B\u3044", romaji: "sei", meaning: "gleich / einheitlich", strokes: 14, explanation: "Getreideähren auf gleicher Höhe – symbolisiert Gleichmäßigkeit und Ordnung. Kommt in 斉 (gleich) und 斎 (Fasten/Reinigung) vor.", examples: ["\u6589","\u658E"] },
  { number: 211, radical: "\u9F52", reading: "\u306F", romaji: "ha", meaning: "Zahn / Z\u00E4hne", strokes: 15, explanation: "Zähne in einem Mund – symbolisiert Alter und Reife (Zähne als Zeichen des Wachstums). Kommt in 歯 (Zahn) und 齢 (Alter) vor.", examples: ["\u6B6F","\u9F62"] },
  { number: 212, radical: "\u9F8D", reading: "\u308A\u3085\u3046", romaji: "ryuu", meaning: "Drache", strokes: 16, explanation: "Ein Drache – das mächtigste Fabelwesen in der ostasiatischen Mythologie. Symbol für kaiserliche Macht, Glück und Regen. 龍/竜 bedeutet »Drache«.", examples: ["\u9F8D","\u7ADC"] },
  { number: 213, radical: "\u4E80", reading: "\u304B\u3081", romaji: "kame", meaning: "Schildkr\u00F6te", strokes: 16, explanation: "Eine Schildkröte – Symbol für Langlebigkeit und Weisheit. Schildkrötenpanzer wurden in der Wahrsagerei verwendet. 亀 bedeutet »Schildkröte«.", examples: ["\u4E80"] },
  { number: 214, radical: "\u9FA0", reading: "\u3084\u304F", romaji: "yaku", meaning: "Fl\u00F6te", strokes: 17, explanation: "Eine Bambusflöte – das letzte und seltenste der 214 Kangxi-Radikale. Symbolisiert harmonische Musik und Vollendung.", examples: [] }
];
